import React, { useRef, useState } from "react";
import emailjs from '@emailjs/browser';
import './css/Contact.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'; // Import Toast styles

const Contact = () => {
  const formRef = useRef();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const sendEmail = async (e) => {
    e.preventDefault();

    setIsSubmitting(true); // Set submitting state for toast
    setErrorMessage(""); // Clear any previous error messages

    try {
      await emailjs
        .sendForm('service_om8k3cg', 'template_hfhxnqd', formRef.current, '4fOhM7nSeH7IAOW7P')
        .then(() => {
          console.log('SUCCESS!');
          toast.success("Email sent successfully!", { autoClose: 5000 }); // Toast with 5 sec auto-close
        });
    } catch (error) {
      console.error('FAILED...', error.text);
      setErrorMessage("Failed to send email. Please try again later.");
      toast.error("Failed to send email!", { autoClose: 5000 }); // Toast with 5 sec auto-close
    } finally {
      setIsSubmitting(false); // Reset submitting state after toast
    }
  };

  return (
    <div className="contact-container">
      <ToastContainer /> {/* Add ToastContainer at the top level */}
      <form ref={formRef} onSubmit={sendEmail}>
        <label>Name</label>
        <input type="text" name="user_name" required />
        <label>Email</label>
        <input type="email" name="user_email" required />
        <label>Message</label>
        <textarea name="messages" required />

        {isSubmitting ? (
          <button disabled>Sending...</button>
        ) : (
          <input type="submit" value="Send" />
        )}
      </form>
      {errorMessage && <div className="error-message">{errorMessage}</div>}
    </div>
  );
};

export default Contact;
